import {useEffect, useState} from "react";
import {getTodos} from "./features/todos/todosReducer";
import {useSelector} from "react-redux";
import {AppRootStateType, useAppDispatch} from "./store/store";
import {TodoType} from "./api/apiTypes";
import {
    App__Title,
    App__Wrapper,
    Error__Wrapper,
    Header__Container,
    Input__Wrapper,
    Modal__Help,
    Modal__Help__Opened,
    Modal__Help__Closed,
    Todo__Wrapper,
    Modal__Help__Input,
    Modal__Help__Header,
    Modal__Help__Texting__Area,
} from "./AppStyles";
import {closeBtn} from "./features/todos/Todo.styles";
import {createTodoHandler} from "./features/todos/AddTodo/AddTodo";

import MainInput from "./features/mainInput/MainInput";
import Theme from "./features/theme/Theme";
import Button from "./features/common/Button";
import {getTasks} from "./features/tasks/tasksReducer";
import {Todo} from "./features/todos/Todo";

function App() {
    const [todo, setTodo] = useState("");
    const [error, setError] = useState("");
    const [modal, setModal] = useState(false);
    const todos = useSelector<AppRootStateType, TodoType[]>((state) => state.todos);
    const dispatch = useAppDispatch();
    const mappedTodos = todos.map((el) => {
        return <Todo key={el.todo_id} todo={el} />;
    });
    useEffect(() => {
        dispatch(getTodos());
        dispatch(getTasks());
    }, []);

    return (
        <App__Wrapper>
            <Header__Container>
                <App__Title>Welcome to Todo App </App__Title>
                <Theme />
            </Header__Container>

            <Input__Wrapper>
                <div>
                    <MainInput
                        placeholder="new todo"
                        todo={todo}
                        setTodo={setTodo}
                        setError={setError}
                        id="mainInput"
                        editTodoHandler={() => createTodoHandler({todo, setTodo, setError, dispatch})}
                        todo_id="7"
                    />
                    <Error__Wrapper>{error}</Error__Wrapper>
                </div>
                <Button name="Add todo" onClick={() => createTodoHandler({todo, setTodo, setError, dispatch})} />
            </Input__Wrapper>
            <Todo__Wrapper>{mappedTodos}</Todo__Wrapper>
            <Modal__Help>
                {modal ? (
                    <Modal__Help__Opened $modal onBlur={()=>setModal(!modal)}>
                        <Modal__Help__Header>
                            <strong>help</strong>
                            <Button icon={closeBtn} onClick={() => setModal(!modal)} />
                        </Modal__Help__Header>
                        <Modal__Help__Texting__Area>
                            vsfvd
                        </Modal__Help__Texting__Area>
                        <Modal__Help__Input>
                            <textarea name="" id=""></textarea>
                        </Modal__Help__Input>
                    </Modal__Help__Opened>
                ) : (
                    <Modal__Help__Closed $modal onClick={() => setModal(!modal)} onBlur={()=>setModal(!modal)}>
                        <strong>help</strong>
                        <Button icon={closeBtn} />
                    </Modal__Help__Closed>
                )}
            </Modal__Help>
        </App__Wrapper>
    );
}

export default App;
