import React, {useEffect, useRef, useState} from "react";
import {
    Modal__Help,
    Modal__Help__Opened,
    Modal__Help__Closed,
    Modal__Help__Input,
    Modal__Help__Header,
    Modal__Help__Texting__Area,
    Modal__Help__Chat,
    Chat__Support,
} from "./HelpChatStyles";
import {SomeMessages, someMessages} from "../../messageData";
import Button from "../common/Button";
import {closeBtn} from "../todos/Todo.styles";

type ModalHelpType = {
    modal: boolean;
    setModal: (value: boolean) => void;
};

export default function HelpChat({modal, setModal}: ModalHelpType) {
    // const [message, setMessage] = useState("");
    // const messagesData = new Promise((res, rej)=>{res})
    // messagesData.then(()=>{
    // })

    let nodeMessages = useRef<HTMLInputElement | null>(null);

    const scrollToLastMessage = async () => {
        const lastChildElement = nodeMessages.current?.lastElementChild;
        await lastChildElement?.scrollIntoView({behavior: "smooth"});
    };
    const chatHandler = () => {
        setModal(!modal);
    };
    
    const mappedJSX = (
        <Modal__Help__Texting__Area ref={nodeMessages}>
            {someMessages.map((el: SomeMessages) => {
                scrollToLastMessage().then(()=>scrollToLastMessage())
                return el.manager ? (
                    <Modal__Help__Chat $user={true} key={new Date().getTime() + el.time}>
                        <Chat__Support>
                            <strong>{el.author}</strong>
                            <pre>{el.message}</pre>
                        </Chat__Support>
                        <pre>{el.time}</pre>
                    </Modal__Help__Chat>
                ) : (
                    <Modal__Help__Chat $user={false} key={new Date().getTime() + el.time}>
                        {el.message}
                        <pre>{el.time}</pre>
                    </Modal__Help__Chat>
                );
            })}
        </Modal__Help__Texting__Area>
    );
    // useEffect(() => {
    //     console.log(1, nodeMessages);
    //     scrollToLastMessage();
    // }, [nodeMessages.current]);
    useEffect(() => {
        const socket = new WebSocket("wss://social-network.samuraijs.com/handlers/ChatHandler.ashx");
        socket.onmessage = () => {
            "fesf";
        };
    }, []);
    return (
        <Modal__Help>
            {modal ? (
                <Modal__Help__Opened $modal onBlur={chatHandler}>
                    <Modal__Help__Header>
                        <strong>help</strong>
                        <Button icon={closeBtn} onClick={chatHandler} />
                    </Modal__Help__Header>
                    {mappedJSX}
                    <Modal__Help__Input>
                        <textarea
                            name=""
                            id=""
                            // onChange={(value) => setMessage(value.currentTarget.value + "")}
                            // value={message}
                        />
                        <Button name="send" onClick={() => ""} />
                    </Modal__Help__Input>
                </Modal__Help__Opened>
            ) : (
                <Modal__Help__Closed $modal onClick={chatHandler} onBlur={chatHandler}>
                    <strong>help</strong>
                    <Button icon={closeBtn} />
                </Modal__Help__Closed>
            )}
        </Modal__Help>
    );
}
