import styled, {keyframes} from "styled-components";

export const App__Title = styled.h1`
    font-size: 1.5em;
    text-align: center;
    --color: #000000;
`;

export const Header__Container = styled.section`
    display: flex;
    justify-content: space-around;
    align-items: center;
    padding: 1em;
    background: #3884c3;
    box-shadow: 0px 15px 10px 2px rgba(34, 60, 80, 0.2);
`;
export const App__Wrapper = styled.div``;

export const Error__Wrapper = styled.div`
    position: absolute;
    padding: 10px;
    color: red;
`;
export const Input__Wrapper = styled.div`
    display: flex;
    padding: 2em;
`;
export const Todo__Wrapper = styled.div`
    display: flex;
    align-items: flex-start;
    flex-wrap: wrap;
`;
export const Div__Container = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
`;

const scaleBottom = keyframes`
    from {
      transform: scale(0.5);
      transform-origin: 100% 100%;
    }
    to {
      transform: scale(1);
      transform-origin: 100% 100%;
    }
  `;

export const Modal__Help = styled.div`
    position: absolute;
    top: 70%;
    left: 85%;
    width: 200px;
    height: 200px;
    display: flex;
    align-items: flex-end;
    justify-content: flex-end;
    border: 1px solid blue;
    background-color: #fff;
    color: black;
    border-radius: var(--border-radius);
`;

export const Modal__Help__Opened = styled.div<{$modal: boolean}>`
    display: flex;
    flex-wrap: wrap;
    position: absolute;
    width: ${p => p.$modal ? "100%" : ""};
    height: ${p => p.$modal ? "100%" : ""};
    /* height: 150px;
    width: 190px; */

    border: 1px solid red;
    animation: ${scaleBottom} 0.4s cubic-bezier(0.39, 0.575, 0.565, 1) both;
    box-shadow: 0px 15px 10px 2px rgba(34, 60, 80, 0.2);
`;
export const Modal__Help__Closed = styled.div`
    position: absolute;
    display: flex;
    flex-wrap: wrap;
    border: 1px solid red;
    /* height: 150px;
    width: 120px; */
    /* height: 150px; */
    /* width: 120px; */
`;
