import React from "react";
import {
    Modal__Help,
    Modal__Help__Opened,
    Modal__Help__Closed,
    Modal__Help__Input,
    Modal__Help__Header,
    Modal__Help__Texting__Area,
    Modal__Help__Chat,
    Chat__Support,
} from "../../AppStyles";
import {SomeMessages, someMessages} from "../../messageData";
import Button from "../common/Button";
import {closeBtn} from "../todos/Todo.styles";

type ModalHelpType = {
    modal: boolean;
    setModal: (value: boolean)=>void;
};

export default function HelpChat ({modal, setModal}: ModalHelpType) {
    const bottomEl = useRef(null);
    return (
        <Modal__Help>
            {modal ? (
                <Modal__Help__Opened $modal onBlur={() => setModal(!modal)}>
                    <Modal__Help__Header>
                        <strong>help</strong>
                        <Button icon={closeBtn} onClick={() => setModal(!modal)} />
                    </Modal__Help__Header>
                    <Modal__Help__Texting__Area>
                        {someMessages.map((el: SomeMessages) => {
                            return el.manager ? (
                                <Modal__Help__Chat $user={true}>
                                    <Chat__Support>
                                        <strong>{el.author}</strong>
                                        <pre>{el.message}</pre>
                                    </Chat__Support>
                                    <pre>{el.time}</pre>
                                </Modal__Help__Chat>
                            ) : (
                                <Modal__Help__Chat $user={false}>
                                    {el.message}
                                    <pre>{el.time}</pre>
                                </Modal__Help__Chat>
                            );
                        })}
                    </Modal__Help__Texting__Area>
                    <Modal__Help__Input>
                        <textarea name="" id=""></textarea>
                    </Modal__Help__Input>
                </Modal__Help__Opened>
            ) : (
                <Modal__Help__Closed $modal onClick={() => setModal(!modal)} onBlur={() => setModal(!modal)}>
                    <strong>help</strong>
                    <Button icon={closeBtn} />
                </Modal__Help__Closed>
            )}
        </Modal__Help>
    );
};
