import React, {useRef, useState} from "react";
import {
    Modal__Help,
    Modal__Help__Opened,
    Modal__Help__Closed,
    Modal__Help__Input,
    Modal__Help__Header,
    Modal__Help__Texting__Area,
    Modal__Help__Chat,
    Chat__Support,
} from "../../AppStyles";
import {SomeMessages, someMessages} from "../../messageData";
import Button from "../common/Button";
import {closeBtn} from "../todos/Todo.styles";

type ModalHelpType = {
    modal: boolean;
    setModal: (value: boolean) => void;
};

export default function HelpChat({modal, setModal}: ModalHelpType) {
    const [mess, setMess] = useState(someMessages)
    const nodeMessages = useRef<HTMLInputElement | null>(null);
    const scrollToLastMessage = () => {
        const lastChildElement = nodeMessages.current?.lastElementChild;
        console.log(nodeMessages.current, modal);
        lastChildElement?.scrollIntoView({behavior: "smooth"});
    };
    if (!modal) {
        scrollToLastMessage();
    }
    const chatHandler = () => {
        // if (modal) {
        //     setMessages([])
        // }
        scrollToLastMessage();
        setModal(!modal);
    };
    return (
        <Modal__Help>
            {modal ? (
                <Modal__Help__Opened $modal onBlur={chatHandler}>
                    <Modal__Help__Header>
                        <strong>help</strong>
                        <Button icon={closeBtn} onClick={chatHandler} />
                    </Modal__Help__Header>
                    <Modal__Help__Texting__Area ref={nodeMessages}>
                        {mess.map((el: SomeMessages) => {
                            return el.manager ? (
                                <Modal__Help__Chat $user={true} key={new Date().getTime() + el.time}>
                                    <Chat__Support>
                                        <strong>{el.author}</strong>
                                        <pre>{el.message}</pre>
                                    </Chat__Support>
                                    <pre>{el.time}</pre>
                                </Modal__Help__Chat>
                            ) : (
                                <Modal__Help__Chat $user={false} key={new Date().getTime() + el.time}>
                                    {el.message}
                                    <pre>{el.time}</pre>
                                </Modal__Help__Chat>
                            );
                        })}
                    </Modal__Help__Texting__Area>
                    <Modal__Help__Input>
                        <textarea name="" id=""></textarea>
                    </Modal__Help__Input>
                </Modal__Help__Opened>
            ) : (
                <Modal__Help__Closed $modal onClick={chatHandler} onBlur={chatHandler}>
                    <strong>help</strong>
                    <Button icon={closeBtn} />
                </Modal__Help__Closed>
            )}
        </Modal__Help>
    );
}
